def xpath_query(request):
    xpath = request.GET.get('xpath')
    xml = request.GET.get('xml')

    # validate inputs
    if not xpath or not xml:
        return HttpResponse(status=400, content='Missing 'xml' or 'xpath' parameter')

    # parse xml payload
    try:
        root = etree.parse(StringIO(xml)).getroot()
    except Exception as e:
        return HttpResponse(status=400, content=f'Invalid XML format: {str(e)}')

    # perform XPath query
    results = root.xpath(f"/tag[@id='{xpath}']")

    # return results as string
