import java.io.*;
import java.lang.*;
import java.util.*;
import java.math.*;


class ID3{
	/**
	* Write content to the file. If file does not exist, it will create one.
	*/
	public static void writeToFile(String content, String fileName){
			try{
			// Create file object
			File file = new File(fileName);

			// If file does not exist, create one
			if(!file.exists()){
				file.createNewFile();
			}

			// Create FileWriter object
			FileWriter fw = new FileWriter(file.getAbsoluteFile(), true);

			// Create BufferedWriter object
			BufferedWriter bw = new BufferedWriter(fw);

			// Write content to file
			bw.write(content);

			// Add new line after content
			bw.newLine();

			// Close BufferedWriter
			bw.close();
		}catch(IOException e){
			e.printStackTrace();
		}
	}

	/**
	* Read data from the file
	*/
	public static List<String> readFromFile(String fileName){
		List<String> records = new ArrayList<String>();
		try{
			// Create file object
			File file = new File(fileName);

			// If file does not exist, throw exception
			if(!file.exists()){
				throw new FileNotFoundException("File does not exist!");
			}

			// Create FileReader object
			FileReader fr = new FileReader(file);

			// Create BufferedReader object
			BufferedReader br = new BufferedReader(fr);

			String line;
			// Read data line by line and add to list
			while((line = br.readLine()) != null){
				records.add(line);
			}

			// Close BufferedReader
			br.close();
		}catch(IOException e){
			e.printStackTrace();
		}

		return records;
	}

	/**
	* Main function
	*/
	public static void main(String[] args){
		// Write sample data to file
		writeToFile("Apple, Red, Sweet, Round, Small", "sample_data.txt");
		writeToFile("Orange, Orange, Sour, Round, Small", "sample_data.txt");
		writeToFile("Banana, Yellow, Sweet, Oval, Large", "sample_data.txt");

		// Read data from file
		List<String> records = readFromFile("sample_data.txt");

		// Print data on console
		for(String record : records){
			System.out.println(record);
		}
	}
}

