	/**
	* Converts from an int array to an Integer array.
	*/
	public static Integer[] asObjectArray(int[] array){
			Integer[] result = new Integer[array.length];
		for (int i = 0; i < array.length; i++){
			result[i] = Integer.valueOf(array[i]);
		}
		return result;
	}

	/**
	* Finds the maximum difference between two elements in the array.
	* @param array The input array.
	* @return The maximum difference between two elements in the array.
	*/
	public static int maxDifference(int[] array){
		if (array == null || array.length < 2){
			return 0;
		}
		Integer[] objectArray = asObjectArray(array);
		Arrays.sort(objectArray, Collections.reverseOrder());
		int maxDiff = 0;
		for (int i = 0; i < objectArray.length - 1; i++){
			int diff = objectArray[i] - objectArray[i+1];
			if (diff > maxDiff){
				maxDiff = diff;
			}
		}
		return maxDiff;
	}

