class ID176{
	/**
	* Returns true if the bit at the given position is 1 and false otherwise.
	*/
	public static boolean getBinaryState(int targetInteger, int position){
			return ((targetInteger >> position) & 1) == 1;
	}

	/**
	* Returns the decimal equivalent of the binary represented by the given boolean array.
	*/
	public static int getDecimalValue(boolean[] binaryArray){
		// Initialize a variable to store the result
		int decimalValue = 0;

		// Iterate through the binary array from left to right
		for(int i = binaryArray.length - 1; i >= 0; i--){
			// If the current bit is a 1, add the corresponding power of 2 to the result
			if(binaryArray[i]){
				decimalValue += Math.pow(2, binaryArray.length - 1 - i);
			}
		}

		return decimalValue;
	}

	public static void main(String[] args){
		Scanner sc = new Scanner(System.in);

		// Read in the target decimal number and the number of bits to consider
		int targetDecimal = sc.nextInt();
		int numBitsConsidered = sc.nextInt();

		// Initialize a boolean array to represent the binary of the target number
		boolean[] binaryArray = new boolean[numBitsConsidered];

		// Iterate through the specified number of bits and set the corresponding boolean value in the array
		for(int i = 0; i < numBitsConsidered; i++){
			binaryArray[i] = getBinaryState(targetDecimal, i);
		}

		// Print the decimal representation of the binary array
		System.out.println(getDecimalValue(binaryArray));
	}
}

