import java.util.*;
import java.math.*;

class ID176{
    /**
    * Returns true if the bit at the given position is 1 and false otherwise.
    */
    public static boolean getBinaryState(int targetInteger, int position){
        return ((targetInteger >> position) & 1) == 1;
    }

    /**
    * Returns the decimal equivalent of the binary represented by the given boolean array.
    */
    public static int getDecimalValue(boolean[] binaryArray){
        // Initialize a variable to store the result
        int decimalValue = 0;

        // Iterate through the binary array from left to right
        for(int i = binaryArray.length - 1; i >= 0; i--){
            // If the current bit is a 1, add the corresponding power of 2 to the result
            if(binaryArray[i]){
                decimalValue += Math.pow(2, binaryArray.length - 1 - i);
            }
        }

        return decimalValue;
    }

    public static void main(String[] args) throws Exception{
        // Use a BufferedReader to read input from the console
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in, "UTF-8"));

        // Read the number of test cases
        int numTestCases = Integer.parseInt(br.readLine().trim());

        // Iterate through each test case
        for(int i = 0; i < numTestCases; i++){
            // Read the input for the current test case
            String[] input = br.readLine().trim().split(" ");
            int targetInteger = Integer.parseInt(input[0]);
            int numBits = Integer.parseInt(input[1]);

            // Initialize a boolean array to store the binary representation of the target integer
            boolean[] binaryArray = new boolean[numBits];

            // Iterate through each bit position and set the corresponding value in the boolean array
            for(int j = 0; j < numBits; j++){
                binaryArray[j] = getBinaryState(targetInteger, numBits - 1 - j);
            }

            // Calculate the decimal value of the binary representation
            int decimalValue = getDecimalValue(binaryArray);

            // Print the result
            System.out.println(decimalValue);
        }
    }
}

