	/**
	* Decode base64 string to bytes
	*/
	public static byte[] base64decode(String base64string){
		try{
			return base64string.getBytes("iso-8859-1");
		} catch (UnsupportedEncodingException e)
		{
			e.printStackTrace();
		}
		return null;
	}

